function MagneticWrap({ children, strength = 0.2, className }) {
  const ref = useRef(null);
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const handleMove = (e) => {
    const rect = ref.current.getBoundingClientRect();
    setOffset({ x: (e.clientX - rect.left - rect.width / 2) * strength, y: (e.clientY - rect.top - rect.height / 2) * strength });
  };
  return (
    <div ref={ref} className={className} onMouseMove={handleMove} onMouseLeave={() => setOffset({ x: 0, y: 0 })}
      style={{ display: "inline-block", transform: `translate(${offset.x}px, ${offset.y}px)`, transition: `transform 0.5s ${T.ease}` }}>
      {children}
    </div>
  );
}

function TiltCard({ children, style: extraStyle = {}, className = "" }) {
  const ref = useRef(null);
  const [tilt, setTilt] = useState({ x: 0, y: 0 });
  const [shine, setShine] = useState({ x: 50, y: 50 });
  const [hovering, setHovering] = useState(false);

  const handleMove = (e) => {
    const rect = ref.current.getBoundingClientRect();
    const x = (e.clientX - rect.left) / rect.width;
    const y = (e.clientY - rect.top) / rect.height;
    setTilt({ x: (y - 0.5) * -6, y: (x - 0.5) * 6 });
    setShine({ x: x * 100, y: y * 100 });
  };

  return (
    <div ref={ref} data-h className={className}
      onMouseEnter={() => setHovering(true)}
      onMouseLeave={() => { setHovering(false); setTilt({ x: 0, y: 0 }); }}
      onMouseMove={handleMove}
      style={{
        transform: `perspective(800px) rotateX(${tilt.x}deg) rotateY(${tilt.y}deg) ${hovering ? "translateZ(6px)" : ""}`,
        transition: hovering ? "none" : `transform 0.6s ${T.ease}`,
        position: "relative",
        ...extraStyle,
      }}>
      <div style={{
        position: "absolute", inset: 0, borderRadius: "inherit",
        background: `radial-gradient(circle at ${shine.x}% ${shine.y}%, rgba(200,200,220,${hovering ? 0.07 : 0}) 0%, transparent 55%)`,
        pointerEvents: "none", transition: hovering ? "none" : "background 0.6s",
        zIndex: 1,
      }} />
      <div style={{ position: "relative", zIndex: 2 }}>{children}</div>
    </div>
  );
}

