function FeaturedScroll() {
  const scrollRef = useRef(null);
  const [canScrollL, setCanScrollL] = useState(false);
  const [canScrollR, setCanScrollR] = useState(true);

  const checkScroll = () => {
    const el = scrollRef.current;
    if (!el) return;
    setCanScrollL(el.scrollLeft > 10);
    setCanScrollR(el.scrollLeft < el.scrollWidth - el.clientWidth - 10);
  };

  const scroll = (dir) => {
    const el = scrollRef.current;
    if (!el) return;
    el.scrollBy({ left: dir * 340, behavior: "smooth" });
  };

  useEffect(() => {
    const el = scrollRef.current;
    if (el) { el.addEventListener("scroll", checkScroll); checkScroll(); }
    return () => el?.removeEventListener("scroll", checkScroll);
  }, []);

  return (
    <section style={{ padding: "60px 0 80px", position: "relative" }}>
      {/* Header */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "0 clamp(24px, 6vw, 100px)", marginBottom: "32px" }}>
        <div style={{ display: "flex", alignItems: "center", gap: "16px" }}>
          <div style={{ width: "32px", height: "1px", background: T.silver }} />
          <span style={{ fontFamily: T.fontMono, fontSize: "0.68rem", color: T.textDim, letterSpacing: "0.18em", textTransform: "uppercase" }}>Featured Work</span>
        </div>
        <div style={{ display: "flex", gap: "8px" }}>
          <button data-h onClick={() => scroll(-1)} style={{
            width: "36px", height: "36px", borderRadius: "50%",
            border: `1px solid ${canScrollL ? T.borderHover : T.border}`,
            background: "transparent", color: canScrollL ? T.silverBright : T.textDim,
            cursor: canScrollL ? "pointer" : "default", display: "flex", alignItems: "center", justifyContent: "center",
            fontSize: "0.9rem", transition: `all 0.3s ${T.ease}`, opacity: canScrollL ? 1 : 0.35,
          }}>←</button>
          <button data-h onClick={() => scroll(1)} style={{
            width: "36px", height: "36px", borderRadius: "50%",
            border: `1px solid ${canScrollR ? T.borderHover : T.border}`,
            background: "transparent", color: canScrollR ? T.silverBright : T.textDim,
            cursor: canScrollR ? "pointer" : "default", display: "flex", alignItems: "center", justifyContent: "center",
            fontSize: "0.9rem", transition: `all 0.3s ${T.ease}`, opacity: canScrollR ? 1 : 0.35,
          }}>→</button>
        </div>
      </div>

      {/* Horizontal scroll container */}
      <div ref={scrollRef} className="featured-scroll" style={{
        display: "flex", gap: "16px", overflowX: "auto", overflowY: "hidden",
        padding: "0 clamp(24px, 6vw, 100px) 12px",
        scrollSnapType: "x mandatory", scrollbarWidth: "none", msOverflowStyle: "none",
      }}>
        {FEATURED_PROJECTS.map((p, i) => (
          <FeaturedCard key={i} project={p} index={i} />
        ))}
      </div>
    </section>
  );
}

